    histogram = {}

    #Starting the loop
    for letter in letters:
        #Checking if the key is in the dictionary
        if letter in histogram:
            histogram[letter] += 1
        #If the key is not in the dictionary, check if the count of that letter is the most frequent one
        elif histogram.get(letter) is not None:
            histogram[letter] += 1
        #If the key is not in the dictionary and the count of the letter is the most frequent one, replace that
        #most frequent key with a list containing both key and its count
        else:
            new_keys = histogram.keys()
            #Checking if the new key is one of the already existing keys
            for key in new_keys:
                if new_keys.count(key) > new_keys.count(letter):
                    histogram[key] = histogram[new_keys[new_keys.index(key)]]
                    histogram[new_keys[new_keys.index(key)]] = None
            histogram[letter] = histogram[new_keys[new_keys.index(letter)]]
            histogram[new_keys[new_keys.index(letter)]] = None

