    0
    >>> how_many_times('aaa', 'a')
    3
    >>> how_many_times('aaaa', 'aa')
    3
    """
    def overlap(s: str, t: str) -> bool:
        """ True if string s contains at least 1 occurence of string t, False otherwise.
        """
        if s == t:
            return True
        if len(s) == len(t):
            return False
        if len(s) > len(t):
            return overlap(s[len(t):], t)
        if len(s) < len(t):
            return overlap(s, t[len(s):])
        return overlap(s[1:], t[1:])
    return sum(overlap(s, substring) for s in string.split())

