    smallest = numbers[0]
    largest = numbers[-1]
    for num in numbers:
        if num < smallest:
            smallest = num
        if num > largest:
            largest = num

    # Scale numbers so that smallest is 0 and largest is 1
    def scale(number: float) -> float:
        return number / (largest - smallest)

    return [scale(x) for x in numbers]

