    """
    Checks if given string is a palindrome
    >>> is_palindrome('')
    True
    >>> is_palindrome('aba')
    True
    >>> is_palindrome('aaaaa')
    True
    >>> is_palindrome('zbcd')
    False
    """

    def is_palindrome_internal(text, start, end):
        if start >= end:
            return True

        if text[start] == text[end]:
            return is_palindrome_internal(text, start + 1, end - 1)
        else:
            return False

    return is_palindrome_internal(text, 0, len(text) - 1)

