    """
    if n == 0:
        return 1
    elif n < 0:
        return modp(-n, p)
    else:
        return reduce(lambda a, b: a*b, map(lambda i: pow(2, i), range(1, n+1)), p)

def modp(n: int, p: int):
    """Return 2^n modulo p (be aware of numerics).
    >>> modp(3, 5)
    3
    >>> modp(1101, 101)
    2
    >>> modp(0, 101)
    1
    >>> modp(3, 11)
    8
    >>> modp(100, 101)
    1
    """
    if n == 0:
        return 1
    if p < 2:
        raise ValueError('p < 2')
    if p == 2:
        return n % 2
    m = n - 1;
    while m % 2 == 0:
        m //= 2
    d = m // p;
    m %= p;
    if m == 0:
        return d
    while m!= 0:
        m //= 2
        d *= 2
    return d

def modp(n: int, p: int):
    """Return 2^n modulo p (be aware of numerics).
    >>> modp(3, 5)
    3
    >>> modp(1101, 101)
    2
    >>> modp(0, 101)
    1
    >>> modp(3, 11)
    8
    >>> modp(100, 101)
    1
    """
    assert 0 < n < 2**32 and 0 < p < 2**32
    if n == 0:
        return 1
    if p > n:
        raise ValueError("p must be smaller than n")
    if p == 2:
        return n - 1
    if p < 256:
        return (n & p - 1) % p
    if p < 2**8 - 1:
        return n % (p & -(p & -n))
    if p < 2**16 - 1:
        return (n & p - 1) % (p & -(p & -n))
    if p < 2**32 - 1:
        return (n & p - 1) % (p & -(p & -n))

def modp(n: int, p: int):
    """Return 2^n modulo p (be aware of numerics).
    >>> modp(3, 5)
    3
    >>> modp(1101, 101)
    2
    >>> modp(0, 101)
    1
    >>> modp(3, 11)
    8
    >>> modp(100, 101)
    1
    """
    if n == 0:
        return 1
    if p <= 32:
        return pow(2, n, p)
    if p % 4!= 3:
        return pow(2, n, p)
    tmp = p
    while tmp > 3:
        tmp = tmp >> 1
        tmp = tmp & tmp - 1
    return pow(2, n, tmp)

def modp(n: int, p: int):
    """Return 2^n modulo p (be aware of numerics).
    >>> modp(3, 5)
    3
    >>> modp(1101, 101)
    2
    >>> modp(0, 101)
    1
    >>> modp(3, 11)
    8
    >>> modp(100, 101)
    1
    """
    if p < 0:
        raise ValueError('p cannot be negative')
    if n < 0:
        raise ValueError('n cannot be negative')

    if p == 2:
        return n % 2
    if p == 3:
        return n % 3
    if p == 5:
        return n % 5

    if n == 0:
        return 0
    if n == 1:
        return 1

    result = 1
    while n > 0:
        if n % 2 == 0:
            result *= p
        n //= 2
    return result

def modp(n: int, p: int):
    """Return 2^n modulo p (be aware of numerics).
    >>> modp(3, 5)
    3
    >>> modp(1101, 101)
    2
    >>> modp(0, 101)
    1
    >>> modp(3, 11)
    8
    >>> modp(100, 101)
    1
    """
    if n < 0:
        raise ValueError("n must be >= 0, but got %i" % n)
    if n == 0:
        return 1
    if n == 1:
        return 0
    if n % 2 == 0:
        return modp(n / 2, p)
    else:
        return p - modp(n - 1, p)
