def derivative(xs: list):
    """ xs represent coefficients of a polynomial.
    xs[0] + xs[1] * x + xs[2] * x^2 + ....
     Return derivative of this polynomial in the same form.
    >>> derivative([3, 1, 2, 4, 5])
    [1, 4, 12, 20]
    >>> derivative([1, 2, 3])
    [2, 6]
    """
    if len(xs) < 2:
        return xs
    else:
        return xs[1:] + [xs[0] + xs[-1]] + [xs[-1] * xs[-2]] + derivative(xs[2:])

def derivative(xs: list):
    """ xs represent coefficients of a polynomial.
    xs[0] + xs[1] * x + xs[2] * x^2 + ....
     Return derivative of this polynomial in the same form.
    >>> derivative([3, 1, 2, 4, 5])
    [1, 4, 12, 20]
    >>> derivative([1, 2, 3])
    [2, 6]
    """    # case of degree 0
    if len(xs) == 0:
        return xs

    # case of degree 1
    if len(xs) == 1:
        return xs

    # case of degree 2
    elif len(xs) == 2:
        return xs[0] + xs[1] * xs[0]

    # recursive call
    else:
        return xs[0] + xs[1] * xs[0] + xs[2] * (xs[0] * xs[0])

def derivative(xs: list):
    """ xs represent coefficients of a polynomial.
    xs[0] + xs[1] * x + xs[2] * x^2 + ....
     Return derivative of this polynomial in the same form.
    >>> derivative([3, 1, 2, 4, 5])
    [1, 4, 12, 20]
    >>> derivative([1, 2, 3])
    [2, 6]
    """    def derivative_rec(xs_i: list):
        if not xs_i:
            return []
        return xs_i[1:] + [sum(xs_i[:i])] + [sum(xs[i + 1:])] + derivative_rec(xs_i[1:])
    return map(derivative_rec, xs)

