def derivative(xs: list):
    """ xs represent coefficients of a polynomial.
    xs[0] + xs[1] * x + xs[2] * x^2 + ....
     Return derivative of this polynomial in the same form.
    >>> derivative([3, 1, 2, 4, 5])
    [1, 4, 12, 20]
    >>> derivative([1, 2, 3])
    [2, 6]
    """
    def _derivative(xs, n):
        if n == 0:
            return []
        elif n == 1:
            return xs

        result = []
        for i in xs:
            result.append(i)
            for j in _derivative(xs, n - 1):
                result.append(i * j)

        return result

    return _derivative(xs, len(xs) - 1)
