    """
    if len(arr) == 0:
        return 0
    if len(arr) == 1:
        return 1
    if len(arr) == 2:
        return 2
    if len(arr) == 3:
        return 3
    if len(arr) == 4:
        return 4
    if len(arr) == 5:
        return 5
    if len(arr) == 6:
        return 6
    return 0

