def encode_cyclic(s: str):
    """
    returns encoded string by cycling groups of three characters.
    """
    # split string to groups. Each of length 3.
    groups = [s[(3 * i):min((3 * i + 3), len(s))] for i in range((len(s) + 2) // 3)]
    # cycle elements in each group. Unless group has fewer elements than 3.
    groups = [(group[1:] + group[0]) if len(group) == 3 else group for group in groups]
    return "".join(groups)


def decode_cyclic(s: str):
    """
    takes as input string encoded with encode_cyclic function. Returns decoded string.
    """
    # decode string encoded with encode_cyclic function. If there are groups of three characters
    # return groups. If there are groups of two characters return groups. Otherwise, return empty list.
    groups = []
    if len(s) == 3:
        groups.append(s[(3 * i):min((3 * i + 3), len(s))] for i in range((len(s) + 2) // 3)])
    if len(s) == 2:
        groups.append(s[(2 * i):min((2 * i + 2), len(s))] for i in range((len(s) + 2) // 2)])
    if len(s) == 1:
        groups.append(s[(1 * i):min((1 * i + 1), len(s))] for i in range((len(s) + 1) // 1))

    return "".join(groups)
