def histogram(test):
    """Given a string representing a space separated lowercase letters, return a dictionary
    of the letter with the most repetition and containing the corresponding count.
    If several letters have the same occurrence, return all of them.

    Example:
    histogram('a b c') == {'a': 1, 'b': 1, 'c': 1}
    histogram('a b b a') == {'a': 2, 'b': 2}
    histogram('a b c a b') == {'a': 2, 'b': 2}
    histogram('b b b b a') == {'b': 4}
    histogram('') == {}

    """
    # A list of the letter occurrences in the string
    occurrence = []
    # A dictionary of the letter with the most occurrences
    occurrences = {}
    # A dictionary of the letter count, where each key is the letter and the value is the
    # number of occurrences of the letter
    counts = {}

    for letter in test:
        # If the letter is not a letter in the dictionary, add it to the dictionary
        if not occurrence or occurrence.count(letter)!= len(occurrences[letter]):
            occurrence.append(letter)
            occurrences[letter] = len(occurrences[letter])
            counts[letter] = occurrences[letter]

    return {letter:counts.get(letter,0) for letter in occurrence}

