def is_palindrome(text: str):
    """
    Checks if given string is a palindrome
    >>> is_palindrome('')
    True
    >>> is_palindrome('aba')
    True
    >>> is_palindrome('aaaaa')
    True
    >>> is_palindrome('zbcd')
    False
    """
    # Check for empty string
    if len(text) == 0:
        return True

    # Check for consecutive characters
    for i in range(len(text) - 1):
        if text[i]!= text[i + 1]:
            return False

    # Check for special characters
    if text[0] in string.punctuation:
        return False

    # Check for the same characters in reverse
    for i in range(len(text) - 1):
        if text[i]!= text[i - 1]:
            return False

    # Check for characters that are not consecutive
    for i in range(len(text) - 1):
        if text[i]!= text[i + 1]:
            return False

    # Check for characters that are not special
    for i in range(len(text) - 1):
        if text[i] not in string.letters:
            return False

    # Check for characters that are not palindrome
    if len(text)!= len(text) % 2:
        return False

    # Check for palindrome
    return True
