def is_palindrome(text: str):
    """
    Checks if given string is a palindrome
    >>> is_palindrome('')
    True
    >>> is_palindrome('aba')
    True
    >>> is_palindrome('aaaaa')
    True
    >>> is_palindrome('zbcd')
    False
    """
    if len(text) == 1:
        return True

    d = {}
    for i in range(len(text)):
        d[i] = text[i]

    if d[0]!= d[-1]:
        return False

    for i in range(len(d) - 1, -1, -1):
        if d[i]!= d[i + 1]:
            return False

    return True

def is_palindrome(text: str):
    """
    Checks if given string is a palindrome
    >>> is_palindrome('')
    True
    >>> is_palindrome('aba')
    True
    >>> is_palindrome('aaaaa')
    True
    >>> is_palindrome('zbcd')
    False
    """
    def is_overlapping(s1, s2):
        if len(s1)!= len(s2):
            return False
        for i in range(len(s1)-1, -1, -1):
            if s1[i]!= s2[i+1]:
                return False
        return True
    return all(is_overlapping(i, j) for i in range(len(text), len(text)-1, -1) for j in range(len(text)-1, -1, -1))

def is_palindrome(text: str):
    """
    Checks if given string is a palindrome
    >>> is_palindrome('')
    True
    >>> is_palindrome('aba')
    True
    >>> is_palindrome('aaaaa')
    True
    >>> is_palindrome('zbcd')
    False
    """
    if len(text) == 0:
        return True

    def process_character(character: str):
        for i in range(len(character)):
            if character[i]!= character[i-1]:
                return False
        return True

    for character in text:
        if process_character(character):
            return True

    return False
