def digits(n):
    """Given a positive integer n, return the product of the odd digits.
    Return 0 if all digits are even.
    For example:
    digits(1)  == 1
    digits(4)  == 0
    digits(235) == 15
    """
    if n % 2 == 0:
        return 0
    if n % 4 == 0:
        return 1
    return 2 * digits(n - 1) + digits(n - 2) + digits(n - 3) + digits(n - 4)

