def histogram(test):
    """Given a string representing a space separated lowercase letters, return a dictionary
    of the letter with the most repetition and containing the corresponding count.
    If several letters have the same occurrence, return all of them.

    Example:
    histogram('a b c') == {'a': 1, 'b': 1, 'c': 1}
    histogram('a b b a') == {'a': 2, 'b': 2}
    histogram('a b c a b') == {'a': 2, 'b': 2}
    histogram('b b b b a') == {'b': 4}
    histogram('') == {}

    """
    # Init
    res = {}
    for letter in test.lower():
        res[letter] = 0
    # Count
    for letter in test.lower():
        res[letter] += 1
    # Sort
    res.sort()
    # Return
    return res

def histogram(test):
    """Given a string representing a space separated lowercase letters, return a dictionary
    of the letter with the most repetition and containing the corresponding count.
    If several letters have the same occurrence, return all of them.

    Example:
    histogram('a b c') == {'a': 1, 'b': 1, 'c': 1}
    histogram('a b b a') == {'a': 2, 'b': 2}
    histogram('a b c a b') == {'a': 2, 'b': 2}
    histogram('b b b b a') == {'b': 4}
    histogram('') == {}

    """
    def _get_max_repeats(string):
        """Return the number of times a character appears in the string."""
        max_repeats = 0
        for char in string:
            if char in string.lower():
                max_repeats += 1
        return max_repeats

    def _get_max_count(string):
        """Return the number of occurrences of a character in the string."""
        max_count = 0
        for char in string:
            if char in string.lower():
                max_count += 1
        return max_count

    max_repeats = _get_max_repeats(test)
    max_count = _get_max_count(test)

    return {letter: max_repeats, count: max_count for letter, count in list(enumerate(test))}

