def is_palindrome(text: str):
    """
    Checks if given string is a palindrome
    >>> is_palindrome('')
    True
    >>> is_palindrome('aba')
    True
    >>> is_palindrome('aaaaa')
    True
    >>> is_palindrome('zbcd')
    False
    """
    if len(text) < 2:
        return False
    i, j = 0, 0
    temp = ''
    while j < len(text):
        if text[j] == text[i]:
            if i == 0:
                return False
            else:
                temp += text[i - 1]
            j += 1
        else:
            i += 1
    if text[i]!= text[j]:
        return False
    if temp == text:
        return True
    else:
        return False
